mapper.select:
select log_id, ticket_id, action, old_status, new_status, operator_id, operator_name, remark, create_time from biz_ticket_log

     and ticket_id = #{ticketId}
     and action = #{action}

order by log_id desc

select log_id, ticket_id, action, old_status, new_status, operator_id, operator_name, remark, create_time
from biz_ticket_log

     and ticket_id = #{ticketId}
     and action = #{action}
     and create_time >= #{params.beginTime}
     and create_time <= #{params.endTime}

order by log_id desc

select count(*) from biz_ticket_log
where ticket_id = #{param1} and action = #{param2}
  and date(create_time) = current_date
