mapper.select:
select * from dc_rack

     and room_id = #{roomId}
     and code like concat('%',#{code},'%')
     and status = #{status}
    and del_flag='0'

order by code

mapper.update:
update dc_rack
room_id=#{roomId}, code=#{code}, u_total=#{uTotal}, power_rated=#{powerRated}, power_current=#{powerCurrent}, status=#{status}, update_by=#{updateBy}, update_time=#{updateTime}, remark=#{remark},
where rack_id=#{rackId}
